package basic;

public class DataTypeEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Data Types");
		char ch='p';
		char ch1=0;
		System.out.println("Character:"+ch1);
		//-127 to 127
		byte b=119;
		System.out.println("Byte:"+b);
		short s=290;
		System.out.println("Short:"+s);
		int i=123456789;
		int ii=0;
		System.out.println("Integer:"+i);
		//19 Digit
		long l=123456789897899999L;
		long ll=0;
		System.out.println("Long:"+ll);
		float f=89.99F;
		System.out.println("Float:"+f);
		double d=789.676d;
		double dd=0.0;
		System.out.println("Double:"+d);
		boolean bol=false;
		System.out.println("Boolean:"+bol);


	}

}
